export function formatTime (time) {
  const addZero = (m) => {
    return m < 10 ? '0' + m : m
  }
  if ((`${time}`).length === 10) {
    time = window.parseInt(time) * 1000
  } else {
    time = +time
  }
  const d = new Date(time)
  return (
    `${d.getFullYear()}-${addZero(d.getMonth() + 1)}-${addZero(d.getDate())} ${addZero(d.getHours())}:${addZero(d.getMinutes())}:${addZero(d.getSeconds())}`
  )
