// url 为 http://localhost:8080/welcome时
// 输出 Hello Guest
router.GET("/welcome", func(c *gin.Context) {	
		name := c.DefaultQuery("name", "Guest") // 可设置默认值	
		// 是 c.Request.URL.Query().Get("lastname") 的简写
    lastname := c.Query("lastname") 
    fmt.Println("Hello %s", name)})
```

